function icon( $icon, $class = null ){
  if( $icon === 'condense' || $icon === 'explode' ){ // replace mis-scaled svgs
    return '<svg class="icon'.( $class ? ' icon--'.$class : '').'" viewBox="0 0 72 72"><use xlink:href="#'.$icon.'"/></svg>';
  }
  return '<svg class="icon'.( $class ? ' icon--'.$class : '').'" viewBox="0 0 128 128"><use xlink:href="#'.$icon.'"/></svg>';
}
